import React, { useState } from 'react';
import { TextField, Button } from '@mui/material';

const NewUserForm = () => {
  // Estado para armazenar os dados do novo usuário
  const [user, setUser] = useState({
    personType: '',
    name: '',
    cpf: '',
    phone: '',
    email: '',
    addresses: [],
  });

  // Estado para armazenar o novo endereço digitado
  const [newAddress, setNewAddress] = useState('');

  // Função para atualizar o estado do novo usuário com os dados do formulário
  const handleChange = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value });
  };

  // Função para adicionar o novo endereço à lista de endereços
  const handleAddAddress = () => {
    setUser({ ...user, addresses: [...user.addresses, newAddress] });
    setNewAddress('');
  };

  // Função para enviar o formulário de novo usuário (será chamada pelo botão "Salvar")
  const handleSubmit = () => {
    // Implemente aqui a lógica para enviar os dados do novo usuário para o backend
    console.log('Novo usuário enviado:', user);
  };

  return (
    <div>
      <h1>Inserir novo usuário</h1>
      <div>
        <label>Tipo de pessoa:</label>
        <select name="personType" value={user.personType} onChange={handleChange}>
          <option value="fisica">Física</option>
          <option value="juridica">Jurídica</option>
        </select>
      </div>
      <div>
        <TextField
          label="Nome"
          name="name"
          value={user.name}
          onChange={handleChange}
        />
      </div>
      <div>
        <TextField
          label="CPF"
          name="cpf"
          value={user.cpf}
          onChange={handleChange}
        />
      </div>
      <div>
        <TextField
          label="Telefone"
          name="phone"
          value={user.phone}
          onChange={handleChange}
        />
      </div>
      <div>
        <TextField
          label="Email"
          name="email"
          value={user.email}
          onChange={handleChange}
        />
      </div>
      <div>
        <Button variant="contained" onClick={handleAddAddress}>Adicionar novo endereço</Button>
        <ul>
          {/* Exibir a lista de endereços */}
          {user.addresses.map((address, index) => (
            <li key={index}>{address}</li>
          ))}
        </ul>
      </div>
      <div>
        <Button variant="contained" color="primary" onClick={handleSubmit}>Salvar</Button>
      </div>
    </div>
  );
};

export default NewUserForm;
