import React, { useState } from 'react';
import { TextField, Grid, Button, FormControl, InputLabel, Select, MenuItem } from '@mui/material';

const PaginaEnderecos = () => {
  const [tipoPessoa, setTipoPessoa] = useState('pessoaFisica');
  const [input1, setInput1] = useState('');
  const [input2, setInput2] = useState('');
  const [enderecos, setEnderecos] = useState([{endereco: "Rua Belo Horizonte, 2830", endereco: "Rua Belo Horizonte, 2830"}]);

  const handleChange = (event) => {
    setTipoPessoa(event.target.value);
  };

  const handleAddEndereco = () => {
    setEnderecos([...enderecos, { endereco: 'Novo Endereço' }]);
  };

  const handleRemoveEndereco = (index) => {
    const updatedEnderecos = [...enderecos];
    updatedEnderecos.splice(index, 1);
    setEnderecos(updatedEnderecos);
  };

  return (
    <div>
      <h1>Página com Inputs e Endereços</h1>
      <Grid container spacing={2} alignItems="center">
        {/* Select e 2 inputs na mesma linha */}
        <Grid item xs={4}>
          <FormControl fullWidth>
            <InputLabel id="tipo-pessoa-label">Tipo de Pessoa</InputLabel>
            <Select
              labelId="tipo-pessoa-label"
              id="tipo-pessoa"
              value={tipoPessoa}
              onChange={handleChange}
            >
              <MenuItem value="pessoaFisica">Pessoa Física</MenuItem>
              <MenuItem value="pessoaJuridica">Pessoa Jurídica</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={4}>
          <TextField
            label="Input 1"
            fullWidth
            value={input1}
            onChange={(e) => setInput1(e.target.value)}
          />
        </Grid>
        <Grid item xs={4}>
          <TextField
            label="Input 2"
            fullWidth
            value={input2}
            onChange={(e) => setInput2(e.target.value)}
          />
        </Grid>

        {/* 2 inputs em outra linha */}
        <Grid item xs={6}>
          <TextField label="Input 3" fullWidth />
        </Grid>
        <Grid item xs={6}>
          <TextField label="Input 4" fullWidth />
        </Grid>
      </Grid>

      {/* Grid com listagem de endereços */}
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={6}>
          <h3>Endereços</h3>
        </Grid>
        <Grid item xs={6}>
          <Button variant="contained" onClick={handleAddEndereco}>Adicionar Endereço</Button>
        </Grid>
        {enderecos.map((endereco, index) => (
          <Grid item xs={12} key={index}>
            <span>{endereco.endereco}</span>
            <Button variant="contained" color="secondary" onClick={() => handleRemoveEndereco(index)}>Remover</Button>
          </Grid>
        ))}
      </Grid>

      <Button variant="contained" color="primary">Salvar</Button>
    </div>
  );
};

export default PaginaEnderecos;
